import React, { Component } from "react";
import * as actions from "./../store/actions/all_actions";

class AddTodo extends Component {
  state = { text: "" };
  render() {
    console.log("Add");
    return (
      <form onSubmit={this.handleSubmit} className="form form-horizental ">
        <input
          type="text"
          style={{ transition: "1s" }}
          className="ml-auto mr-auto mt-2 form-control col-3"
          value={this.state.text}
          onChange={this.handleChange}
        />
        <button type="submit" className="btn btn-primary m-2">
          Add Todo
        </button>
      </form>
    );
  }
  handleChange = e => {
    this.setState({ text: e.target.value });
  };
  handleSubmit = e => {
    e.preventDefault();
    const { text } = this.state;
    if (text.length > 5) {
      window.store.dispatch({
        type: actions.ADD_TO_DO,
        todo: { text: text, status: false }
      });
    }
  };
}

export default AddTodo;
