import * as actions from "./../actions/all_actions"
import {
    produce
} from "immer"

const initialState = {
    todos: [{
        text: "Osama Inayat is Good boy",
        status: false
    }],
    filter: "NOT_COMPLETEDCC"
}

export default function todoReducer(state = initialState, action) {
    switch (action.type) {
        case actions.ADD_TO_DO:
            return produce(state, draftState => {
                draftState.todos.push(action.todo);
            })
        case actions.SET_FILTER:
            return produce(state, draftState => {
                draftState.filter = action.filter
            })
        case actions.TOGGLE_TO_DO:


            return produce(state, draftState => {
                console.log(draftState.todos[action.index].status);
                draftState.todos[action.index].status = !draftState.todos[action.index].status
            })
        case actions.REMOVE_TO_DO:
            return produce(state, draftState => {
                draftState.todos.splice(action.index, 1);
            })
        default:
            return state;
    }
}
