import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTimes, faCheck } from "@fortawesome/free-solid-svg-icons";
import * as actions from "./../store/actions/all_actions";
import { connect } from "react-redux";
const ShowTodo = props => {
  const filteredTodo = filterTodos(props.todos, props.filter);
  // console.log(t);
  return (
    <div className="container">
      <div className="btn-group float-right">
        <button
          className="btn  btn-sm btn-secondary"
          onClick={() => setFilter("COMPLETED")}
        >
          Completed
        </button>
        <button
          onClick={() => setFilter("NOT_COMPLETED")}
          className="btn btn-sm btn-danger"
        >
          Not Completed
        </button>
        <button
          className="btn btn-sm btn-primary"
          onClick={() => setFilter("SHOW_ALL")}
        >
          Show All
        </button>
      </div>
      <table className="table table-dark table-bordered text-center">
        <thead>
          <tr>
            <th>#Sr</th>
            <th>TODO</th>
            <th>STATUS</th>
            <th>Time Added</th>
            <th>DELETE</th>
          </tr>
        </thead>
        <tbody>
          {filteredTodo.map((todo, index) => (
            <tr key={index}>
              <td>{index + 1}</td>
              <td style={{ textDecoration: todo.status ? "line-through" : "" }}>
                {todo.text}
              </td>
              <td>
                {
                  <FontAwesomeIcon
                    onClick={() => toggleTodo(index)}
                    style={{ fontSize: "25px", color: "green" }}
                    icon={todo.status ? faCheck : faTimes}
                  />
                }
              </td>
              <td>
                <button
                  className="btn btn-sm btn-danger"
                  onClick={() => removeTodo(index)}
                >
                  Remove
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};
function removeTodo(index) {
  window.store.dispatch({ type: actions.REMOVE_TO_DO, index: index });
}
function toggleTodo(index) {
  window.store.dispatch({ type: actions.TOGGLE_TO_DO, index: index });
}
function setFilter(filter) {
  window.store.dispatch({ type: actions.SET_FILTER, filter: filter });
}
function filterTodos(todos, filter) {
  switch (filter) {
    case "SHOW_ALL":
      console.log("Called 1:" + filter);
      return todos;
    case "COMPLETED":
      console.log("Called 2:" + filter);
      return todos.filter(todo => todo.status === true);
    case "NOT_COMPLETED":
      console.log("Called 3:" + filter);
      return todos.filter(todo => todo.status === false);
    default:
      return todos;
  }
}

const mapStateToProps = state => {
  return state;
};
export default connect(mapStateToProps)(ShowTodo);
