import React from "react";
import { connect } from "react-redux";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTimes, faCheck, faDice } from "@fortawesome/free-solid-svg-icons";

const ShowTodo = ({ todos, filter }) => {
  return (
    <div className="container">
      <div className="btn-group">
        <button className="btn btn-sm btn-primary">Show All</button>
        <button className="btn btn-sm btn-danger">Completed</button>
        <button className="btn btn-sm btn-dark">Not Completed</button>
      </div>
      <table className="table table-dark  table-bordered table-collapsed text-center">
        <thead>
          <tr>
            <th>SR</th>
            <th>TODO </th>
            <th>STATUS</th>
          </tr>
        </thead>
        <tbody>
          {todos.map((todo, index) => {
            return (
              <tr key={index}>
                <td>{index + 1}</td>
                <td>{todo.text}</td>
                <td>
                  <FontAwesomeIcon
                    onClick
                    icon={todo.status ? faCheck : faTimes}
                  />
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
};

const mapStateToProps = state => {
  return state;
};

export default connect(mapStateToProps)(ShowTodo);
