import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTimes, faCheck } from "@fortawesome/free-solid-svg-icons";
import * as actions from "./../store/actions/all_actions";
import { connect } from "react-redux";
const ShowTodo = ({ todos, filter }) => {
  const filteredTodo = filterTodo(todos, filter);
  console.log("Application");
  console.log(filteredTodo);
  return (
    <div className="container">
      <table className="table table-dark text-center">
        <thead>
          <tr>
            <th>#Sr</th>
            <th>TODO</th>
            <th>STATUS</th>
            <th>DELETE</th>
          </tr>
        </thead>
        <tbody>
          {filteredTodo.map((todo, index) => (
            <tr key={index}>
              <td>{index + 1}</td>
              <td>{todo.text}</td>
              <td>
                {
                  <FontAwesomeIcon
                    onClick={toggleTodo(index)}
                    style={{ fontSize: "25px", color: "green" }}
                    icon={todo.status ? faCheck : faTimes}
                  />
                }
              </td>
              <td>
                <button
                  className="btn btn-danger btn-sm"
                  onClick={removeTodo(index)}
                >
                  Remove
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};
function removeTodo(index) {
  window.store.dispatch({ type: actions.REMOVE_TO_DO, index: index });
}
function toggleTodo(index) {
  window.store.dispatch({ type: actions.TOGGLE_TO_DO, index: index });
}
function filterTodo(todos, filter) {
  switch (filter) {
    case "SHOW_ALL":
      console.log("0");
      return todos;
    case "COMPLETED":
      console.log("1");
      return todos.filter(todo => todo.status);
    case "NOT_COMPLETED:":
      console.log("2");
      return todos.filter(todo => !todo.status);
    default:
      return todos;
  }
}

const mapStateToProps = state => {
  return state;
};
export default connect(mapStateToProps)(ShowTodo);
